using Microsoft.VisualStudio.TestTools.UnitTesting;
using Neo;
using System;
using System.Linq;
using System.Numerics;
using System.Text;

namespace OpCodeConverter.Test
{
    [TestClass]
    public class UnitTest1
    {
        [TestMethod]
        public void TestMethod1()
        {
            //常见的 invocation
            var script = "DECKiNs7nm9rKamTRSQpjuRHmmKZlX0n1m89FfDzOvgcQIe7JfMnsg/4Ss1yHVwTxpmDjs1GWRcRyntZ06S81fIF";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHDATA1 8a88db3b9e6f6b29a9934524298ee4479a6299957d27d66f3d15f0f33af81c4087bb25f327b20ff84acd721d5c13c699838ecd46591711ca7b59d3a4bcd5f205\r\n");
        }

        [TestMethod]
        public void TestMethod2()
        {
            //常见的 verification
            var script = "EQwhA9q4TBJD7AGrJQDhqMehVGom1zRigYCwz2TnK/d2U2mXEQtBE43vrw==";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHDATA1 03dab84c1243ec01ab2500e1a8c7a1546a26d734628180b0cf64e72bf776536997\r\nSYSCALL Neo.Crypto.CheckMultisigWithECDsaSecp256r1\r\n");
        }

        [TestMethod]
        public void TestMethod3()
        {
            //NEO 转账的 Scripts
            var script = "AgDh9QUMFHuv2LNVxgojhF87HIzMuK8GKakRDBTUzRIZzo4XK1AnOCPXmaNl+raw5BPADAh0cmFuc2ZlcgwU+fgUl8P5tiupP3PHEdQbHu/1DCNBYn1bUjk=";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHINT32 100000000\r\nPUSHDATA1 0x11a92906afb8cc8c1c3b5f84230ac655b3d8af7b\r\nPUSHDATA1 0xe4b0b6fa65a399d7233827502b178ece1912cdd4\r\nPUSHDATA1 transfer\r\nPUSHDATA1 0x230cf5ef1e1bd411c7733fa92bb6f9c39714f8f9\r\nSYSCALL System.Contract.Call\r\n");
        }

        [TestMethod]
        public void TestMethod4()
        {
            //NEP-5 转账的 Scripts
            var script = "AwDyBSoBAAAADBTUzRIZzo4XK1AnOCPXmaNl+raw5AwU1M0SGc6OFytQJzgj15mjZfq2sOQTwAwIdHJhbnNmZXIMFPn4FJfD+bYrqT9zxxHUGx7v9QwjQWJ9W1I5";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHINT64 5000000000\r\nPUSHDATA1 0xe4b0b6fa65a399d7233827502b178ece1912cdd4\r\nPUSHDATA1 0xe4b0b6fa65a399d7233827502b178ece1912cdd4\r\nPUSHDATA1 transfer\r\nPUSHDATA1 0x230cf5ef1e1bd411c7733fa92bb6f9c39714f8f9\r\nSYSCALL System.Contract.Call\r\n");

        }

        [TestMethod]
        public void TestMethod5()
        {
            //调用 NEP-5 "name" 方法的 Scripts
            var script = "EMAMBG5hbWUMFPn4FJfD+bYrqT9zxxHUGx7v9QwjQWJ9W1I=";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHDATA1 name\r\nPUSHDATA1 0x230cf5ef1e1bd411c7733fa92bb6f9c39714f8f9\r\nSYSCALL System.Contract.Call\r\n");
        }

        [TestMethod]
        public void TestMethod6()
        {
            //部署简单合约的 Scripts
            var script = "";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHDATA2 \r\nPUSHDATA1 570102117022026840\r\nSYSCALL System.Contract.Create\r\n");
        }

        [TestMethod]
        public void TestMethod7()
        {
            //部署 NEP-5 合约的 Scripts
            var script = "";
            var result = "";
            Program.Analysis(script).ForEach(p => result += p + "\r\n");
            Assert.AreEqual(result, "PUSHDATA2 \r\nPUSHDATA1 \r\nSYSCALL System.Contract.Create\r\n");
        }
    }
}
